SELECT departamento.nombre AS "nombre departamento", persona.apellido1, persona.apellido2, persona.nombre FROM departamento RIGHT JOIN persona ON departamento.id=persona.id WHERE persona.tipo="profesor" ORDER BY departamento.nombre, persona.apellido1, persona.apellido2, persona.nombre;
SELECT persona.apellido1, persona.apellido2, persona.nombre, departamento.nombre AS nombre_departamento FROM departamento RIGHT JOIN persona ON departamento.id=persona.id WHERE (persona.tipo="profesor") AND departamento.nombre IS NULL;
SELECT DISTINCT departamento.nombre AS nombre_departamento, profesor.id_profesor FROM departamento LEFT JOIN profesor ON departamento.id=profesor.id_departamento WHERE profesor.id_profesor IS NULL;
SELECT persona.id, persona.apellido1, persona.apellido2, persona.nombre, persona.tipo, asignatura.nombre AS "nombre asignatura" FROM persona LEFT JOIN asignatura ON persona.id=asignatura.id_profesor WHERE asignatura.nombre IS NULL AND persona.tipo="profesor";
SELECT asignatura.nombre AS "nombre asignatura", profesor.id_profesor FROM profesor RIGHT JOIN asignatura ON profesor.id_profesor=asignatura.id_profesor WHERE profesor.id_profesor IS NULL;
SELECT departamento.nombre AS nombre_departamento, asignatura.nombre AS nombre_asignatura, asignatura.id_profesor, asignatura.curso  FROM asignatura RIGHT JOIN departamento ON departamento.id=asignatura.id_profesor;